import ZeiterfassungAPI from "../api/ZeiterfassungAPI";
import * as React from 'react';
import {Component} from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import PersonDetail from "./PersonDetail";
import Paper from '@mui/material/Paper';


class PersonenList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            personen: [],
            error: null
        };
    }

    /**
     * Wir fetchen alle PersonenBOs von der REST-API, der durch die server.py bereitgestellt wird
    */
    getPersonen = () => {
        ZeiterfassungAPI.getAPI().getPersonen()
            .then(x =>
            this.setState({
                personen: x,
                error: null
            })).catch(e =>

            this.setState({
                personen: [],
                error: e
            }))
    }

    render() {
        const {personen, error} = this.state;
        return (
            <div style={{backgroundColor: "red", width:"100%"}}>

                <List sx={{width: "100%", maxWidth: 360, bgcolor: "background.paper"}}>
                    {
                        personen.map(x => 
                        <PersonDetail vorname={x.getVorname} nachname={x.getNachname}></PersonDetail>) 
                    }

                </List>

            </div>
        )
    }
}

export default PersonenList;
